import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useProfile } from '../context/ProfileContext';
import debounce from 'lodash.debounce';
import './Profile.css';

const Profile = () => {
    const { id } = useParams();
    const { user } = useAuth();
    const { profilePicture, setProfilePicture } = useProfile();
    const [username, setUsername] = useState('');
    const [bio, setBio] = useState('');
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [images, setImages] = useState([]);
    const [modalImage, setModalImage] = useState(null);
    const [activeImageId, setActiveImageId] = useState(null);
    const [likes, setLikes] = useState({});
    const [comments, setComments] = useState({});
    const [commentInput, setCommentInput] = useState('');
    const [userLikedImages, setUserLikedImages] = useState(new Set());
    const [commentLikes, setCommentLikes] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const [tempUsername, setTempUsername] = useState('');
    const [tempBio, setTempBio] = useState('');
    const [tempProfilePicture, setTempProfilePicture] = useState(null);
    const [profilePictureFile, setProfilePictureFile] = useState(null);
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [file, setFile] = useState(null);

    const debouncedOpenModal = useCallback(
        debounce((image) => {
            console.log('Opening modal for image ID:', image.id);
            setModalImage(image); // Add this line
            setActiveImageId(image.id);
            setModalOpen(true);
            fetchImageDetails(image.id);
        }, 300),
        []
    );

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) {
            setMessage('Please select a file to upload.');
            return;
        }

        const formData = new FormData();
        formData.append('profile_picture', file);

        const token = localStorage.getItem('token');
        try {
            const response = await fetch('http://localhost:3000/upload_profile_picture', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                body: formData
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            console.log('Profile picture updated:', data);
            setMessage('Profile picture updated successfully.');

            // Add a delay before re-fetching the profile picture
            setTimeout(fetchProfilePicture, 2000); // Increased delay to 2000ms
        } catch (error) {
            console.error('Error updating profile picture:', error);
            setMessage('Failed to update profile picture.');
        }
    };

    const fetchProfilePicture = async () => {
        const token = localStorage.getItem('token');
        try {
            const response = await fetch('http://localhost:3000/profile_picture', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            setProfilePicture(data.profile_picture);
            setTempProfilePicture(data.profile_picture); // Update tempProfilePicture as well
        } catch (error) {
            console.error('Error fetching profile picture:', error);
        }
    };

    useEffect(() => {
        fetchProfilePicture();
    }, []);

    useEffect(() => {
        // This useEffect will run whenever profilePicture changes
        if (profilePicture) {
            console.log('Profile picture updated:', profilePicture);
        }
    }, [profilePicture]);

    return (
        <div className="profile-container">
            <div className="profile-header-wrapper">
                <div className="profile-header">
                    {!isLoading && (
                        <>
                            <img 
                                src={`http://localhost:3000/${profilePicture}`} 
                                alt={`${username}'s profile`} 
                                className="profile-profile-picture" 
                                onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = '/default-avatar.png';
                                }}
                            />
                        </>
                    )}
                    <div className="profile-info">
                        <div className="profile-top">
                            <h1 className="profile-username">{username}</h1>
                            {user && user.id === parseInt(id) && (
                                <button className="profile-edit-btn" onClick={() => setIsEditing(true)}>
                                    Edit Profile
                                </button>
                            )}
                        </div>

                        <div className="profile-stats">
                            <div className="stat-item">
                                <span className="stat-number">0</span>
                                <span className="stat-label">Following</span>
                            </div>
                            <div className="stat-item">
                                <span className="stat-number">0</span>
                                <span className="stat-label">Followers</span>
                            </div>
                            <div className="stat-item">
                                <span className="stat-number">0</span>
                                <span className="stat-label">Likes</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            {isEditing && (
                <div className="profile-edit-form">
                    <div className="profile-picture-section">
                        <img 
                            src={`http://localhost:3000/${profilePicture}`} 
                            alt="Profile" 
                            className="profile-edit-picture" 
                        />
                        <input type="file" onChange={handleFileChange} />
                        <button onClick={handleUpload}>Upload</button>
                        {message && <p>{message}</p>}
                    </div>
                    <div className="profile-edit-field">
                        <label>Username</label>
                        <input
                            type="text"
                            value={tempUsername}
                            onChange={(e) => setTempUsername(e.target.value)}
                            className="profile-edit-input"
                        />
                    </div>
                    <div className="profile-edit-field">
                        <label>Bio</label>
                        <textarea
                            value={tempBio}
                            onChange={(e) => setTempBio(e.target.value)}
                            className="profile-edit-input"
                            rows={3}
                        />
                    </div>
                    <div className="profile-edit-actions">
                        <button 
                            className="profile-save-btn"
                            onClick={handleSubmit}
                            disabled={loading}
                        >
                            {loading ? 'Saving...' : 'Save'}
                        </button>
                    </div>
                    {message && (
                        <div className="profile-message">
                            {message}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Profile;
