import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate, NavLink, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useProfile } from '../context/ProfileContext';
import './Header.css';

const Header = () => {
    const { user, logout } = useAuth();
    const { profilePicture } = useProfile();
    const navigate = useNavigate();
    const location = useLocation();
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);
    const [headerProfilePic, setHeaderProfilePic] = useState('/default-avatar.png');
    const [headerPosition, setHeaderPosition] = useState('side');

    useEffect(() => {
        const fetchProfilePicture = async () => {
            if (!user?.id) return;

            try {
                const response = await fetch('/profile_picture', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (response.ok) {
                    const data = await response.json();
                    setHeaderProfilePic(data.profile_picture || '/default-avatar.png');
                }
            } catch (error) {
                console.error('Profile picture fetch error:', error);
            }
        };

        fetchProfilePicture();
    }, [user]);

    return (
        <header className={`header ${headerPosition}`}>
            <nav>
                <div className="nav-left">
                    <Link to="/" className="logo">Image Generator</Link>
                </div>

                <div className="nav-right">
                    {user ? (
                        <>
                            <NavLink to="/gallery">Gallery</NavLink>
                            <NavLink to="/imagegenerator">Create</NavLink>
                            <div className="profile-menu" ref={dropdownRef}>
                                <img 
                                    src={headerProfilePic}
                                    alt="Profile"
                                    onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                />
                                {isDropdownOpen && (
                                    <div className="dropdown-menu">
                                        <Link to={`/profile/${user.id}`}>Profile</Link>
                                        <Link to="/settings">Settings</Link>
                                        <button onClick={logout}>Logout</button>
                                    </div>
                                )}
                            </div>
                        </>
                    ) : (
                        <>
                            <Link to="/login">Login</Link>
                            <Link to="/signup">Sign Up</Link>
                        </>
                    )}
                </div>
            </nav>
        </header>
    );
};

export default Header;
