import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../context/AuthContext'; // Adjust path to your AuthContext
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './ImageGenerator.css';
import LoraSelector from '../components/LoraSelector';


const ImageGenerator = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [prompt, setPrompt] = useState('');
  const [negativePrompt, setNegativePrompt] = useState('');
  const [cfgScale, setCfgScale] = useState(7);
  const [aspectRatio, setAspectRatio] = useState('Portrait');
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [enableUpscale, setEnableUpscale] = useState(false); // State for upscaling
  const [spiral] = useState("Generating your image");
  const [spiral2] = useState("Generating your image");
  const spiralRef = useRef(null);
  const spiral2Ref = useRef(null);
  const [selectedLoras, setSelectedLoras] = useState({});
  const [isLoraOpen, setIsLoraOpen] = useState(false);
  const [categories, setCategories] = useState([]); // State for categories
  const [sampler, setSampler] = useState("DPM++ 2M SDE");
  const [upscaler, setUpscaler] = useState("R-ESRGAN 4x+");
  const [initImage, setInitImage] = useState(null);
  const [isImageToImage, setIsImageToImage] = useState(false);
  const [denoisingStrength, setDenoisingStrength] = useState(0.75);
  const [steps, setSteps] = useState(20); // Add state for steps

  const ANIMATION_DURATION = 2000; // 2 seconds for full animation
  const ANIMATION_OFFSET = 500; // 0.5 second offset
  const SPIRAL2_OFFSET = 4000; // Additional offset for second spiral

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const { exp } = jwtDecode(token);
      if (Date.now() >= exp * 1000) {
        localStorage.removeItem('token');
        localStorage.removeItem('refreshToken');
        navigate('/login');
      } else {
        setLoading(false);
      }
    } catch (error) {
      navigate('/login');
    }
  }, [navigate]);

  // First spiral animation
  useEffect(() => {
    if (loading && spiralRef.current) {
        spiralRef.current.innerHTML = '';

        spiral.split('').forEach((char, i) => {
            const div = document.createElement('div');
            div.innerText = char;
            div.classList.add('character');
            div.style.setProperty('--i', i + 1);
            // Start animation immediately but keep movement pattern
            div.style.animationDelay = `${(i * ANIMATION_DURATION / 16) - ANIMATION_OFFSET - ANIMATION_DURATION}ms`;
            spiralRef.current.appendChild(div);
        });
    }
}, [loading, spiral]);

  // Second spiral animation
  useEffect(() => {
    if (loading && spiral2Ref.current) {
      spiral2Ref.current.innerHTML = '';

      spiral2.split('').forEach((char, i) => {
        const div = document.createElement('div');
        div.innerText = char;
        div.classList.add('character');
        div.style.setProperty('--i', i + 1);
        div.style.animationDelay = `${-(ANIMATION_OFFSET + SPIRAL2_OFFSET) + (i * ANIMATION_DURATION / 16)}ms`;
        spiral2Ref.current.appendChild(div);
      });
    }

    return () => {
      if (spiral2Ref.current) {
        spiral2Ref.current.innerHTML = '';
      }
    };
  }, [loading, spiral2]);

  const buildLoraString = () => {
    return Object.entries(selectedLoras)
        .map(([id, weight]) => `<lora:${id}:${weight}>`)
        .join(' ');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    const token = localStorage.getItem('token');
    console.log('Token:', token);

    try {
        const decoded = jwtDecode(token);
        console.log('Decoded token:', decoded);

        const dimensions = {
          Portrait: { width: 512, height: 768, hiresWidth: 1024, hiresHeight: 1536 },
          Landscape: { width: 768, height: 512, hiresWidth: 1536, hiresHeight: 1024 },
          Square: { width: 512, height: 512, hiresWidth: 1024, hiresHeight: 1024 }
        };

        const { width, height, hiresWidth, hiresHeight } = dimensions[aspectRatio];

        const loraString = buildLoraString();
        const finalPrompt = `${prompt} ${loraString}`;

        const defaultNegativePrompt = "artist tags, patreon text, text, logo, 3d, monochrome, watermark, text, missing fingers, extra arms, extra hands, extra limbs, easynegative, EasyNegativeV2, negative_hand, japanese text, worst quality, low quality, lowres, jpeg artifacts, bad anatomy, bad hands, watermark, ((extra fingers, door, big head)), long neck, character merging";

        const finalNegativePrompt = `${defaultNegativePrompt}, ${negativePrompt}`;

        const payload = {
            prompt: finalPrompt,
            negativePrompt: finalNegativePrompt,
            cfgScale,
            steps, // Use steps state directly
            sampler_name: sampler || "DPM++ 2M SDE", // Default to new sampler
            width,
            height,
            enable_hr: enableUpscale,
            denoising_strength: 0.5,
            upscale_factor: enableUpscale ? 2 : 1,
            hires_width: enableUpscale ? hiresWidth : width,
            hires_height: enableUpscale ? hiresHeight : height,
            hr_upscaler: upscaler || "SwinIR 4x", // Default to new upscaler
            categories
        };



        const response = await fetch('http://localhost:3000/generate_image', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify(payload)
        });

        if (!response.ok) {
            const errorData = await response.json();
            setError(errorData.error || 'Failed to generate image');
        } else {
            const data = await response.json();
            // Update to use base64 image data directly
            setImage(`data:image/png;base64,${data.image}`);
        }
    } catch (error) {
        console.error('Error:', error);
        setError('Failed to generate image');
    } finally {
        setLoading(false);
    }
};

const validSamplers = [
    "Euler",
    "Euler a",
    "LMS",
    "Heun",
    "DPM2",
    "DPM2 a",
    "DPM++ 2S a",
    "DPM++ 2M",
    "DPM++ SDE",
    "DPM++ 2M SDE",
    "DPM++ 2M SDE Karras",
    "DPM++ 3M SDE",
    "DPM++ 3M SDE Karras",
    "DPM fast",
    "DPM adaptive",
    "LMS Karras",
    "DPM2 Karras",
    "DPM2 a Karras",
    "DPM++ 2S a Karras",
    "DPM++ 2M Karras",
];

  return (
    <div className="image-generator">
        <div className="image-generator-container">
            {/* Form Side */}
            <div className="image-generator-form-container">
                <div className="content-wrapper">
                    <h2 className="image-generator-heading">Generate Image</h2>
                    <form onSubmit={handleSubmit} className="image-generator-form">
                        <div className="image-generator-form-group">
                            <label className="image-generator-label" htmlFor="prompt">Prompt:</label>
                            <input
                                type="text"
                                id="prompt"
                                value={prompt}
                                onChange={(e) => setPrompt(e.target.value)}
                                required 
                                className="image-generator-input"
                            />
                        </div>
                        <div className="image-generator-form-group">
                            <label className="image-generator-label" htmlFor="negativePrompt">Negative Prompt:</label>
                            <input
                                type="text"
                                id="negativePrompt"
                                value={negativePrompt}
                                onChange={(e) => setNegativePrompt(e.target.value)}
                                placeholder="what to exclude"
                                className="image-generator-input"
                            />
                        </div>
                        <button 
                            type="button"
                            className={`lora-settings-button ${Object.keys(selectedLoras).length > 0 ? 'active' : ''}`}
                            onClick={() => setIsLoraOpen(true)}
                        >
                            Lora Settings
                        </button>

                        <LoraSelector 
                            selectedLoras={selectedLoras}
                            setSelectedLoras={setSelectedLoras}
                            isOpen={isLoraOpen}
                            onClose={() => setIsLoraOpen(false)}
                        />
                        <div className="image-generator-creative-section">
                            <label className="image-generator-label">Creative Control</label>
                            <div className="image-generator-creative-buttons">
                                <button
                                    type="button"
                                    className={`image-generator-creative-btn ${cfgScale === 1 ? 'selected' : ''}`}
                                    onClick={() => setCfgScale(1)}
                                >
                                    Very Creative
                                </button>
                                <button
                                    type="button"
                                    className={`image-generator-creative-btn ${cfgScale === 7 ? 'selected' : ''}`}
                                    onClick={() => setCfgScale(7)}
                                >
                                    Creative
                                </button>
                                <button
                                    type="button"
                                    className={`image-generator-creative-btn ${cfgScale === 15 ? 'selected' : ''}`}
                                    onClick={() => setCfgScale(15)}
                                >
                                    Subtle
                                </button>
                                <button
                                    type="button"
                                    className={`image-generator-creative-btn ${cfgScale === 30 ? 'selected' : ''}`}
                                    onClick={() => setCfgScale(30)}
                                >
                                    Strict
                                </button>
                            </div>
                        </div>
                        <div className="image-generator-aspect-section">
                            <label className="image-generator-label">Aspect Ratio</label>
                            <div className="image-generator-aspect-buttons">
                                <button
                                    type="button"
                                    className={`image-generator-aspect-btn ${aspectRatio === 'Portrait' ? 'selected' : ''}`}
                                    onClick={() => setAspectRatio('Portrait')}
                                >
                                    Portrait
                                </button>
                                <button
                                    type="button"
                                    className={`image-generator-aspect-btn ${aspectRatio === 'Landscape' ? 'selected' : ''}`}
                                    onClick={() => setAspectRatio('Landscape')}
                                >
                                    Landscape
                                </button>
                                <button
                                    type="button"
                                    className={`image-generator-aspect-btn ${aspectRatio === 'Square' ? 'selected' : ''}`}
                                    onClick={() => setAspectRatio('Square')}
                                >
                                    Square
                                </button>
                            </div>
                        </div>

                        {/* Upscale Toggle Section */}
                        <div className="image-generator-upscale-section">
                            <label className="image-generator-label">Upscale</label>
                            <div className="image-generator-upscale-buttons"></div>
                            <button
                                type="button"
                                className={`image-generator-upscale-btn ${enableUpscale ? 'selected' : ''}`}
                                onClick={() => setEnableUpscale((prev) => !prev)}
                            >
                                {enableUpscale ? 'On' : 'Off'}
                            </button>
                        </div>

                        <div className="image-generator-form-group">
                            <label className="image-generator-label">Steps:</label>
                            <select 
                                value={steps} 
                                onChange={(e) => setSteps(parseInt(e.target.value))}
                                className="image-generator-select"
                            >
                                <option value="20">20 Steps</option>
                                <option value="30">30 Steps</option>
                                <option value="40">40 Steps</option>
                                <option value="50">50 Steps</option>
                            </select>
                        </div>

                        <div className="image-generator-form-group">
                            <label className="image-generator-label">Sampler:</label>
                            <select 
                                value={sampler} 
                                onChange={(e) => setSampler(e.target.value)}
                                className="image-generator-select"
                            >
                                {validSamplers.map(s => (
                                    <option key={s} value={s}>{s}</option>
                                ))}
                            </select>
                        </div>

                        <div className="image-generator-form-group">
                            <label className="image-generator-label">Upscaler:</label>
                            <select 
                                value={upscaler} 
                                onChange={(e) => setUpscaler(e.target.value)}
                                className="image-generator-select"
                            >
                                <option value="Latent">Latent</option>
                                <option value="ESRGAN_4x">ESRGAN 4x</option>
                                <option value="R-ESRGAN 4x+">R-ESRGAN 4x+</option>
                                <option value="LDSR">LDSR</option>
                                <option value="4x-UltraSharp">4x UltraSharp</option>
                                <option value="SwinIR 4x">SwinIR 4x</option>
                            </select>
                        </div>

                        <div className="image-generator-form-group">
                            <label className="image-generator-label">Initial Image:</label>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => {
                                    const file = e.target.files[0];
                                    if (file) {
                                        setInitImage(file);
                                        setIsImageToImage(true);
                                    }
                                }}
                                className="image-generator-input"
                            />
                        </div>

                        {isImageToImage && (
                            <div className="image-generator-form-group">
                                <label className="image-generator-label">
                                    Denoising Strength: {denoisingStrength}
                                </label>
                                <input
                                    type="range"
                                    min="0"
                                    max="1"
                                    step="0.05"
                                    value={denoisingStrength}
                                    onChange={(e) => setDenoisingStrength(parseFloat(e.target.value))}
                                    className="image-generator-range"
                                />
                            </div>
                        )}

                        <button
                            type="submit"
                            disabled={loading}
                            className={`image-generator-submit-btn ${loading ? 'disabled' : ''}`}
                        >
                            {loading ? 'Generating...' : 'Generate Image'}
                        </button>
                    </form>
                </div>
            </div>

            {/* Preview Side */}
            <div className="image-generator-preview-container">
                <div className="content-wrapper">
                    <h2 className="image-generator-heading">Preview</h2>
                    <div className="preview-content">
                        {loading ? (
                            <div className="loading-spinner">Generating...</div>
                        ) : image ? (
                            <img src={image} alt="Generated" className="generated-image" />
                        ) : (
                            <div className="empty-preview">Your image will appear here</div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    </div>
);
}

export default ImageGenerator;
